import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { AuthentifcationService } from '../services/authentifcation.service';
import { Appuser } from '../Model/user.login';
import { AppComponent } from '../app.component';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  userFormGroup!: FormGroup;
  errorMessage : any;




  constructor(private fb: FormBuilder ,private authService : AuthentifcationService,private router:Router ){}


  ngOnInit(): void {
    this.userFormGroup=this.fb.group({
      username : this.fb.control(""),
      password : this.fb.control("")
    });
  }

  handelelogin(){
    let username=this.userFormGroup.value.username;
    let password=this.userFormGroup.value.password;
    this.authService.login(username,password).subscribe({
      next :(appUser :Appuser)=>{
        this.authService.authenticatuser(appUser).subscribe({
          next : (data:Boolean)=>{
            this.router.navigateByUrl("/src/app/cv");
          }
        });
      },
      error : (err)=>{
        this.errorMessage=err;

      }
    });
  }
}
