import { Injectable } from '@angular/core';
import { Appuser } from '../Model/user.login';
import { UntypedFormBuilder } from '@angular/forms';
import { Observable, of, throwError } from 'rxjs';
import { __values } from 'tslib';
import{UUID} from "angular2-uuid";

@Injectable({
  providedIn: 'root'
})
export class AuthentifcationService {
  users: Appuser[]=[];
  authenticatedUser : Appuser | undefined;

  constructor() {
    this.users.push({userId: UUID.UUID(),username:"user1", password:"1234", roles:["USER"]});
    this.users.push({userId: UUID.UUID(),username:"user2", password:"1234", roles:["USER"]});
    this.users.push({userId: UUID.UUID(),username:"admin", password:"1234", roles:["USER","ADMIN"]});
   }
   public login(username : String, password:String):Observable<Appuser>{
     let appUser =this.users.find(u =>u.username==username);
     if(!appUser)return throwError(()=>new Error("user not found"));
     if(appUser.password!=password){
      return throwError(()=>new Error("bad cerdentailes"));

     }
     return of(appUser);
   }
   public authenticatuser( appUser:Appuser):Observable<Boolean>{
    this.authenticatedUser =appUser;
    localStorage.setItem("autUser", JSON.stringify({username:appUser.username,roles : appUser.roles,jwt:"JWT_TOKEN"}))
    return of(true);
   }
   public hasRole(role : String):Boolean{
     return this.authenticatedUser!.roles.includes(role);
   }
   public isAuthentifaction(){
    return this.authenticatedUser!=undefined;
   }
}
