import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Manager } from '../Model/Manager';
import { UserService } from '../servicesA/user.service';

@Component({
  selector: 'app-new-managers',
  templateUrl: './new-managers.component.html',
  styleUrls: ['./new-managers.component.css']
})
export class NewManagersComponent implements OnInit{
  newManagerFormGroup!:FormGroup;


  constructor(private fb:FormBuilder,private userService:UserService){}
  ngOnInit(): void {
    this.newManagerFormGroup=this.fb.group({
      name:this.fb.control(null,[Validators.required, Validators.minLength(4)]),
      email:this.fb.control(null,[Validators.required, Validators.email]),
    });

  }
  handelSaveManager(){
    let manager :Manager=this.newManagerFormGroup.value;
    this.userService.saveUser(manager).subscribe({
      next:data=>{
        alert("Manager has been  successfully saved ")
        this.newManagerFormGroup.reset();
      },
      error :err=>{
        console.log(err);
      }
    });

  }

}
<div class="container">
    <div class ="card" col-md-6 offset-3>
        <div class="card-header">New Manager</div>
        <div class="card-body">
            <form [formGroup]="newManagerFormGroup" (ngSubmit)="handelSaveManager()">
                <div class="mb-3">
                    <label class="form-label">Name:</label>
                    <input type="text" formControlName="name" class="form-control">
                    <span class="text-danger"
                        *ngIf="newManagerFormGroup.controls['name'].touched
                         && newManagerFormGroup.controls['name'].errors
                         && newManagerFormGroup.controls['name'].errors!['required']">
                     Name is Required
                    </span>   
                </div>
                <div class="mb-3">
                    <label class="form-label">Email:</label>
                    <input type="text" formControlName="email" class="form-control">
                    <span class="text-danger"
                        *ngIf="newManagerFormGroup.controls['email'].touched
                         && newManagerFormGroup.controls['email'].errors
                         && newManagerFormGroup.controls['email'].errors!['required']">
                      Email is not valid
                    </span>   
                </div>
                <button [disabled]="!newManagerFormGroup.valid" class="btn btn-info">Save</button>
            </form>
        </div>
    </div>
</div>
