import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { AuthentifcationService } from '../services/authentifcation.service';
import { trigger, state, style, animate, transition } from '@angular/animations';


import { AppComponent } from '../app.component';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  animations: [
    trigger('pulse', [
      state('normal', style({
        transform: 'scale(1)'
      })),
      state('pulse', style({
        transform: 'scale(1.1)'
      })),
      transition('normal => pulse', animate('500ms ease-in-out')),
      transition('pulse => normal', animate('500ms ease-in-out'))
    ])
  ]
})

export class LoginComponent implements OnInit{
  buttonState = 'normal';

  animateButton() {
    this.buttonState = 'pulse';
    setTimeout(() => {
      this.buttonState = 'normal';
    }, 500);
  }

  userFormGroup!: FormGroup;
  errorMessage : any;




  constructor(private fb: FormBuilder ,private authService : AuthentifcationService,private router:Router ){}


  ngOnInit(): void {
    this.userFormGroup=this.fb.group({
      username : this.fb.control(""),
      password : this.fb.control("")
    });
  }

  handelelogin(){
    let username =this.userFormGroup.value.username;
    let pwd =this.userFormGroup.value.password;
    this.authService.login(username,pwd).subscribe({
      next : data=>{
        this.authService.loadProfile(data);
        this.router.navigateByUrl("/admin")
      },
      error:err=>{
        console.log(err);
      }
    });
  }
}
<div class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card shadow">
                <div class="card-header custom-header text-white text-center">Authentication</div>
                <div class="card-body">
                    <form [formGroup]="userFormGroup" (ngSubmit)="handelelogin()">
                        <div class="mb-3">
                            <label for="username" class="form-label">Username:</label>
                            <input type="text" id="username" formControlName="username" class="form-control" placeholder="Enter your username">
                        </div>
                        <div class="mb-3">
                            <label for="password" class="form-label">Password:</label>
                            <input type="password" id="password" formControlName="password" class="form-control" placeholder="Enter your password">
                        </div>
                        <button class="btn btn-success btn-lg btn-block" type="submit" (click)="animateButton()">Login</button>
                    </form>
                </div>
                <div class="card-footer text-center">
                    <span>Not registered? <a routerLink="/register" class="link-info">Register/Signup here</a></span>
                </div>
            </div>
        </div>
    </div>
</div>
