import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup ,Validators} from '@angular/forms';
import { AuthentifcationService } from '../services/authentifcation.service';
import { Appuser } from '../Model/user.login';
import { AppComponent } from '../app.component';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  userFormGroup!: FormGroup;
  errorMessage : any;




  constructor(private fb: FormBuilder ,private authService : AuthentifcationService,private router:Router ){
    this.userFormGroup = this.fb.group({
      username: ['', Validators.required], // Validators.required est ajouté pour rendre le champ obligatoire
      password: ['', Validators.required] // Validators.required est ajouté pour rendre le champ obligatoire
    });
  }


  ngOnInit(): void {
    this.userFormGroup=this.fb.group({
      username : this.fb.control(""),
      password : this.fb.control("")
    });
  }

  handelelogin(){
    let username=this.userFormGroup.value.username;
    let password=this.userFormGroup.value.password;
    this.authService.login(username,password).subscribe({
      next :(appUser :Appuser)=>{
        this.authService.authenticatuser(appUser).subscribe({
          next : (data:Boolean)=>{
            this.router.navigateByUrl("/admin");
          }
        });
      },
      error : (err)=>{
        this.errorMessage=err;

      }
    });
  }
}



<div class="container mt-4">
    <div class="col-md-6 offset-md-3">
        <div class="card">
            <div class="card-header">Authentication</div>
            <div class="text-danger" *ngIf="errorMessage">{{errorMessage}}</div>
            <div class="card-body">
                <form [formGroup]="userFormGroup" (ngSubmit)="handelelogin()">
                    <div class="mb-3">
                        <label class="form-label">Username:</label>
                        <input type="text" formControlName="username" class="form-control" required>
                        <div *ngIf="userFormGroup.get('username')?.invalid && (userFormGroup.get('username').dirty || userFormGroup.get('username').touched)" class="text-danger">
                            Username is required.
                        </div>
                    </div>
                    <div class="mb-3">
                        <label class="form-label">Password:</label>
                        <input type="password" formControlName="password" class="form-control" required>
                        <div *ngIf="userFormGroup.get('password')?.invalid && (userFormGroup.get('password').dirty || userFormGroup.get('password').touched)" class="text-danger">
                            Password is required.
                        </div>
                    </div>
                    <button [disabled]="userFormGroup.invalid" class="btn btn-primary" type="submit">Login</button>
                </form>
            </div>
        </div>
    </div>
</div>
