import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  user = {
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  };
  success = false;

  onSubmit(registerForm: NgForm) {
    if (registerForm.valid) {
      // Envoie des données du formulaire à votre API ou service
      console.log('Form submitted:', this.user);
      // Remplacez ce code par l'appel à votre API ou service pour enregistrer l'utilisateur
      // Exemple :
      // this.userService.registerUser(this.user).subscribe(response => {
      //   console.log('Registration successful:', response);
      //   this.success = true;
      // }, error => {
      //   console.error('Registration failed:', error);
      // });
      // Pour cet exemple, nous simulons simplement la réussite de l'enregistrement
      this.success = true;
    }
  }
}

<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
    <div class="container-fluid">
        <a class="navbar-brand" routerLink="/">Registration and Login System</a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                <li class="nav-item">
                    <a class="nav-link active" aria-current="page" routerLink="/login">Login</a>
                </li>
            </ul>
        </div>
    </div>
</nav>
<br /><br /><br />
<div class="container">
    <div class="row col-md-8 offset-md-2">
        <div class="card">
            <div class="card-header">
                <h2 class="text-center">Registration</h2>
            </div>
            <!-- Angular NgIf directive to show success message -->
            <div *ngIf="success">
                <div class="alert alert-info">
                    You have successfully registered our app!
                </div>
            </div>
            <div class="card-body">
                <!-- Angular form binding and validation -->
                <form (ngSubmit)="onSubmit" #registerForm="ngForm">
                    <div class="form-group mb-3">
                        <label class="form-label">First Name</label>
                        <input class="form-control" id="firstName" name="firstName" placeholder="Enter first name" [(ngModel)]="user.firstName" required>
                        <!-- Angular NgIf directive to show error message -->
                        <p class="text-danger" *ngIf="registerForm.controls['firstName'].invalid && (registerForm.controls['firstName'].dirty || registerForm.controls['firstName'].touched)">First Name is required</p>
                    </div>

                    <!-- Repeat similar pattern for other form fields -->

                    <div class="form-group">
                        <button class="btn btn-primary" type="submit">Register</button>
                        <span>Already registered? <a routerLink="/login">Login here</a></span>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
